CREATE OR REPLACE FUNCTION obter_se_coluna_contido_tabela (nm_tabela_p text, nm_coluna_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1):= 'N';
qt_existe_col_w			bigint;


BEGIN

select	count(*)
into STRICT	qt_existe_col_w
from	user_tab_cols
where	table_name = upper(nm_tabela_p)
and	column_name = upper(nm_coluna_p);

if ( qt_existe_col_w > 0 ) then
	ds_retorno_w:= 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
