SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_col_igual ( ds_dono_p text, nm_tabela_p text, nm_integridade_p text, nm_indice_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
qt_ind_w		bigint;
qt_cons_w		bigint;


BEGIN

if (ie_opcao_p = 'D') then
	select 	count(*)
	into STRICT	qt_ind_w
	from	indice_atributo
	where	nm_tabela 	= nm_tabela_p
	and	nm_indice	= nm_indice_p;

	select	count(*)
	into STRICT	qt_cons_w
	from	integridade_referencial
	where	nm_tabela 			= nm_tabela_p
	and	nm_integridade_referencial	= nm_integridade_p;
else
	select 	count(*)
	into STRICT	qt_ind_w
	from 	user_ind_columns
	where	table_name 	= nm_tabela_p
	and	index_name	= nm_indice_p;

	select	count(*)
	into STRICT	qt_cons_w
	from	user_cons_columns
	where	table_name	= nm_tabela_p
	and	constraint_name	= nm_integridade_p;
end if;

if (qt_ind_w = qt_cons_w) then
	ds_retorno_w		:= 'S';
else
	ds_retorno_w		:= 'S';
end if;
return	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
