CREATE OR REPLACE FUNCTION obter_se_compl_tipo_lote ( cd_estabelecimento_p bigint, cd_tipo_lote_contabil_p bigint) RETURNS varchar AS $body$
DECLARE

	
qt_hist_w				bigint;
ds_retorno_w				varchar(01);


BEGIN

select	count(*)
into STRICT	qt_hist_w
from	historico_padrao_atributo c,
	historico_padrao_empresa_v b,
	estabelecimento a
where	a.cd_estabelecimento		= cd_estabelecimento_p
and	a.cd_empresa			= b.cd_empresa
and	b.cd_historico		= c.cd_historico
and	c.cd_tipo_lote_contabil	= cd_tipo_lote_contabil_p;

if (qt_hist_w > 0) then
	ds_retorno_w			:= 'S';
else
	ds_retorno_w			:= 'N';
end if;

return ds_retorno_w;

End;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
