CREATE OR REPLACE FUNCTION obter_se_componente_convenio (cd_convenio_p bigint, cd_kit_material_p bigint) RETURNS varchar AS $body$
DECLARE



ie_gerar_componente_w	varchar(01)	:= 'S';


BEGIN

select	coalesce(max(ie_gerar_kit), 'S')
into STRICT	ie_gerar_componente_w
from 	CONV_REGRA_COMP_KIT
where	cd_convenio	= cd_convenio_p
and	CD_KIT_MATERIAL	= cd_kit_material_p;

