CREATE OR REPLACE FUNCTION obter_se_componente_kit_conv (cd_kit_material_p bigint, cd_material_p bigint, cd_convenio_p bigint) RETURNS varchar AS $body$
DECLARE



ie_entra_conta_w	varchar(1):= 'N';
cd_estabelecimento_w	smallint;

c01 CURSOR FOR
	SELECT	c.ie_entra_conta
	from	material d,
		componente_kit c,
		kit_material a
	where	a.cd_kit_material	= cd_kit_material_p
	and 	c.cd_material		= cd_material_p
	and	a.cd_kit_material	= c.cd_kit_material
	and	coalesce(c.cd_convenio, coalesce(cd_convenio_p,0)) = coalesce(cd_convenio_p,0)
	and	a.ie_situacao		= 'A'
	and	c.ie_situacao		= 'A'
	and	d.ie_situacao		= 'A'
	and	c.cd_material     	= d.cd_material
	and	(cd_kit_material_p IS NOT NULL AND cd_kit_material_p::text <> '')
	and	((coalesce(c.cd_estab_regra::text, '') = '') or (c.cd_estab_regra = cd_estabelecimento_w))
	order by coalesce(c.cd_convenio,0);


BEGIN
select 	wheb_usuario_pck.get_cd_estabelecimento
into STRICT	cd_estabelecimento_w
;

open c01;
loop
fetch c01 into
	ie_entra_conta_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	ie_entra_conta_w:= ie_entra_conta_w;
	end;
end loop;
close c01;

return	ie_entra_conta_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
