SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_componente_substituto ( nr_lote_producao_p bigint, cd_material_p bigint ) RETURNS varchar AS $body$
DECLARE


cd_material_w		varchar(6);
nr_ficha_tecnica_w	bigint;
nr_seq_componente_w	bigint;
ie_possui_substituto_w	varchar(1) := 'N';


BEGIN

select	max(cd_material)
into STRICT	cd_material_w
from	lote_producao
where	nr_lote_producao = nr_lote_producao_p;

if (cd_material_w IS NOT NULL AND cd_material_w::text <> '') then

	select	max(nr_ficha_tecnica)
	into STRICT	nr_ficha_tecnica_w
	from	ficha_tecnica
	where	cd_material = cd_material_w;

	if (nr_ficha_tecnica_w IS NOT NULL AND nr_ficha_tecnica_w::text <> '') then
		select	max(nr_seq_componente)
		into STRICT	nr_seq_componente_w
		from	ficha_tecnica_componente
		where	nr_ficha_tecnica	=	nr_ficha_tecnica_w
		and	cd_material		=	cd_material_p;

		if (nr_seq_componente_w IS NOT NULL AND nr_seq_componente_w::text <> '') then
			select	coalesce(max('S'),'N')
			into STRICT	ie_possui_substituto_w
			from	ficha_tecnica_comp_subs
			where	nr_ficha_tecnica	=	nr_ficha_tecnica_w
			and	nr_seq_componente	=	nr_seq_componente_w;
		end if;
	end if;
end if;

return	ie_possui_substituto_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
