CREATE OR REPLACE FUNCTION obter_se_compra_pend_entrega ( cd_material_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_pendente_w			varchar(1) := 'N';
ie_entrega_pendente_w		varchar(1) := 'N';
nr_solic_compra_w		bigint;
nr_ordem_compra_w		bigint;
nr_item_oci_w			integer;
qt_existe_w			integer;

c01 CURSOR FOR 
	SELECT	b.nr_solic_compra 
	from	solic_compra_item a, 
		solic_compra b 
	where	a.cd_material = cd_material_p 
	and	coalesce(a.dt_reprovacao::text, '') = '' 
	and	coalesce(b.nr_seq_motivo_cancel::text, '') = '' 
	and	a.nr_solic_compra = b.nr_solic_compra 
	and	b.dt_solicitacao_compra > (clock_timestamp() - interval '90 days') 
	group by b.nr_solic_compra 
	order by b.nr_solic_compra;

c02 CURSOR FOR 
	SELECT	nr_ordem_compra, 
		nr_item_oci 
	from	ordem_compra_item 
	where	cd_material = cd_material_p 
	and	nr_solic_compra = nr_solic_compra_w;


BEGIN 
 
open c01;
loop 
fetch c01 into	 
	nr_solic_compra_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
 
	select	count(*) 
	into STRICT	qt_existe_w 
	from	ordem_compra_item 
	where	cd_material = cd_material_p 
	and	nr_solic_compra = nr_solic_compra_w;
	 
	if (qt_existe_w = 0) then 
		ie_entrega_pendente_w := 'S';
		goto final;
	end if;
	 
	open c02;
	loop 
	fetch c02 into	 
		nr_ordem_compra_w, 
		nr_item_oci_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		begin 
		 
		ie_entrega_pendente_w := obter_se_oci_pend_entrega(nr_ordem_compra_w,nr_item_oci_w);
		 
		if (ie_entrega_pendente_w = 'S') then 
			goto final;
		end if;
		 
		end;
	end loop;
	close c02;
	 
	end;
end loop;
close c01;
 
<<final>> 
 
if (ie_entrega_pendente_w = 'S') then 
	ie_pendente_w := 'S';
end if;
 
return ie_pendente_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
