SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_comp_ja_fechada ( cd_tipo_lote_contabil_p lote_contabil.cd_tipo_lote_contabil%type, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


dt_ref_lote_w		lote_contabil.dt_referencia%type;
dt_referencia_w		timestamp;
ie_comp_fechada_w	varchar(1) := 'N'; -- Starts as not closed
cd_tipo_lote_contabil_w	lote_contabil.cd_tipo_lote_contabil%type;
cd_estabelecimento_w 	estabelecimento.cd_estabelecimento%type;


BEGIN
select	max(cd_tipo_lote_contabil)
into STRICT	cd_tipo_lote_contabil_w
from	tipo_lote_contabil
where	nm_objeto = 'CTB_PLS_CONTAB_PRODUCAO_MEDICA'
and	ie_situacao = 'A';

if (coalesce(cd_tipo_lote_contabil_w::text, '') = '') then

	cd_tipo_lote_contabil_w := cd_tipo_lote_contabil_p;
end if;

cd_estabelecimento_w := wheb_usuario_pck.get_cd_estabelecimento;

-- Search for the biggest date that is already closed and formats the date so it can be used as a parameter
select	trunc(max(dt_referencia), 'month'),
	trunc(dt_referencia_p, 'month')
into STRICT	dt_ref_lote_w,
	dt_referencia_w
from	lote_contabil
where	cd_tipo_lote_contabil = cd_tipo_lote_contabil_w
and	coalesce(cd_estabelecimento, cd_estabelecimento_w) = cd_estabelecimento_w
and	(dt_geracao_lote IS NOT NULL AND dt_geracao_lote::text <> '');

-- If the competency informed is smaller or equals to the last closed competency then the competency is already closed and returns S
if (dt_referencia_w <= dt_ref_lote_w) then

	ie_comp_fechada_w := 'S';
end if;

return ie_comp_fechada_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
