CREATE OR REPLACE FUNCTION obter_se_comp_kit_rep ( cd_kit_material_p bigint, nr_seq_componente_p bigint, cd_medico_p text) RETURNS varchar AS $body$
DECLARE


ie_utiliza_componente_w		varchar(1) := 'S';

c01 CURSOR FOR
SELECT	coalesce(ie_utiliza_componente,'S')
from	regra_componente_kit
where	cd_kit_material		= cd_kit_material_p
and	nr_seq_componente	= nr_seq_componente_p
and	coalesce(cd_medico,coalesce(cd_medico_p,'0')) = coalesce(cd_medico_p,'0')
order by coalesce(cd_medico,'0');


BEGIN

if (cd_kit_material_p IS NOT NULL AND cd_kit_material_p::text <> '') and (nr_seq_componente_p IS NOT NULL AND nr_seq_componente_p::text <> '') then
	open C01;
	loop
	fetch C01 into
		ie_utiliza_componente_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		ie_utiliza_componente_w	:= ie_utiliza_componente_w;
	end loop;
	close C01;

end if;

return ie_utiliza_componente_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
