CREATE OR REPLACE FUNCTION obter_se_com_cliente_vinculado ( nr_seq_cliente_p bigint) RETURNS varchar AS $body$
DECLARE


ie_com_cliente_vinculado_w	varchar(1) := 'N';


BEGIN
if (nr_seq_cliente_p IS NOT NULL AND nr_seq_cliente_p::text <> '') then
	begin
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_com_cliente_vinculado_w
	from	com_cliente_vinculo
	where	nr_seq_cliente = nr_seq_cliente_p;
	end;
end if;
return	ie_com_cliente_vinculado_w;
end;
$body$
