CREATE OR REPLACE FUNCTION obter_se_conduta_lib ( nr_sequencia_p bigint, cd_perfil_p bigint, cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

qt_reg_w	bigint;

BEGIN

select	count(*)
into STRICT	qt_reg_w
from	PEP_CONDUTA_LIB
where	NR_SEQ_CONDUTA = nr_sequencia_p;

if (qt_reg_w	> 0) then
	select	count(*)
	into STRICT	qt_reg_w
	from	PEP_CONDUTA_LIB
	where	NR_SEQ_CONDUTA = nr_sequencia_p
	and	coalesce(cd_perfil,cd_perfil_p)	= cd_perfil_p
	and	coalesce(cd_setor_atendimento,cd_setor_atendimento_p)	= cd_setor_atendimento_p;

	if (qt_reg_w	= 0) then
		return 'N';
	end if;
end if;

return	'S';

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
