CREATE OR REPLACE FUNCTION obter_se_conduta_vazio (nr_atendimento_p bigint, nr_sequencia_p bigint, ie_escala_p bigint, nr_seq_score_flex_p bigint default 0) RETURNS varchar AS $body$
DECLARE

ds_resultado_w	varchar(1) := 'N';
nr_sequencia_w	bigint;


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (ie_escala_p IS NOT NULL AND ie_escala_p::text <> '') then

	select	coalesce(pep_obter_seq_conduta_esc(nr_atendimento_p, nr_sequencia_p, ie_escala_p, nr_seq_score_flex_p),0)
	into STRICT	nr_sequencia_w
	;

	if (nr_sequencia_w <> 0) then

		select	coalesce(max('S'),'N')
		into STRICT	ds_resultado_w
		from	pep_conduta where	nr_sequencia = nr_sequencia_w LIMIT 1;
	end if;

end if;
return	ds_resultado_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
