CREATE OR REPLACE FUNCTION obter_se_config_exame_pac_lib ( cd_estabelecimento_p bigint, cd_perfil_p bigint, cd_pessoa_fisica_p text, cd_setor_usuario_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		    varchar(1)    := 'S';

C01 CURSOR FOR
SELECT	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
from	configuracao_exame_pac a, configuracao_exame_pac_lib b
where	a.nr_sequencia = b.nr_seq_config
and		coalesce(b.cd_estabelecimento, cd_estabelecimento_p) = cd_estabelecimento_p
and   coalesce(b.cd_perfil, cd_perfil_p) = cd_perfil_p
and		coalesce(b.cd_pessoa_fisica, cd_pessoa_fisica_p) = cd_pessoa_fisica_p
and   coalesce(b.cd_setor_usuario, cd_setor_usuario_p) = cd_setor_usuario_p
and (coalesce(b.cd_especialidade::text, '') = '' or obter_se_especialidade_medico(cd_pessoa_fisica_p, b.cd_especialidade) = 'S');


BEGIN

select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
into STRICT	ds_retorno_w
from	configuracao_exame_pac a, configuracao_exame_pac_lib b
where	a.nr_sequencia = b.nr_seq_config;

if (ds_retorno_w = 'N') then

	open C01;
	loop
	fetch C01 into
		ds_retorno_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		ds_retorno_w	:= ds_retorno_w;
		end;
	end loop;
	close C01;
end if;

return	ds_retorno_w;

end;
