CREATE OR REPLACE FUNCTION obter_se_confirma_procedimento ( nr_cirurgia_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_regra_w 			bigint := 0;
nr_seq_proc_interno_w		bigint;


BEGIN

if (nr_cirurgia_p IS NOT NULL AND nr_cirurgia_p::text <> '') then
	select  max(a.nr_seq_proc_interno)
	into STRICT    nr_seq_proc_interno_w
	from    cirurgia a
	where   a.nr_cirurgia = nr_cirurgia_p;

	if (nr_seq_proc_interno_w IS NOT NULL AND nr_seq_proc_interno_w::text <> '') then
		select coalesce(max(nr_sequencia),0)
		into STRICT    nr_seq_regra_w
		from   pepo_proced_confirmacao
		where  nr_seq_proc_interno = nr_seq_proc_interno_w;
	end if;
end if;

return	nr_seq_regra_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
