CREATE OR REPLACE FUNCTION obter_se_congelacao_existe ( nr_seq_congelacao_p text, nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE



ie_existe_w		varchar(2);
qt_existe_w		bigint;
qt_existe_prescr_w	bigint;

BEGIN

select 	count(*)
into STRICT	qt_existe_w
from   	prescr_proc_peca a,
	tipo_amostra b,
	prescr_procedimento c
where  	a.nr_seq_tipo 	= b.nr_sequencia
and	a.nr_prescricao	= c.nr_prescricao
and	a.nr_seq_prescr	= c.nr_sequencia
and	obter_atendimento_prescr(a.nr_prescricao) = obter_atendimento_prescr(nr_prescricao_p)
and	b.ie_tipo_exame 	= 2
and	a.nr_seq_caso_congelacao = nr_seq_congelacao_p;

if (qt_existe_w > 0) then
	begin
	ie_existe_w := 'S';
	select 	count(*)
	into STRICT	qt_existe_prescr_w
	from   	prescr_proc_peca a,
		tipo_amostra b,
		prescr_procedimento c
	where  	a.nr_seq_tipo 	= b.nr_sequencia
	and	a.nr_prescricao	= c.nr_prescricao
	and	a.nr_seq_prescr	= c.nr_sequencia
	and	a.nr_prescricao 	= nr_prescricao_p
	and	(a.nr_seq_caso_congelacao IS NOT NULL AND a.nr_seq_caso_congelacao::text <> '')
	and	b.ie_tipo_exame	= 2;

	if (qt_existe_prescr_w = 0) then
		ie_existe_w := 'C';
	else
		ie_existe_w := 'S';
	end if;
	end;
else
	ie_existe_w := 'N';
end if;


return	ie_existe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
