CREATE OR REPLACE FUNCTION obter_se_conjunto_equip ( nr_seq_conjunto_p bigint, nr_seq_equipamento_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_equipamento_w	bigint;
ds_retorno_w		varchar(1) := 'S';
qt_conjuntos_w	bigint;


BEGIN

select 	count(*)
into STRICT	qt_conjuntos_w
from	cm_equip_conjunto
where	nr_seq_conjunto = nr_seq_conjunto_p;

if (qt_conjuntos_w > 0) then
	select 	count(*)
	into STRICT	qt_conjuntos_w
	from	cm_equip_conjunto
	where	nr_seq_equipamento = nr_seq_equipamento_p
	and	nr_seq_conjunto = nr_seq_conjunto_p;

	if (qt_conjuntos_w = 0)  then
		ds_retorno_w := 'N';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
