CREATE OR REPLACE FUNCTION obter_se_consentido_usuario (NM_USUARIO_P text, NR_SEQUENCIA_P bigint) RETURNS varchar AS $body$
DECLARE

qt_registros bigint;
nr_seq_doc_w bigint;
ret varchar(1);

BEGIN
    select count(*) into STRICT qt_registros
    from
        documento_aprov_prof 
    where
        nr_sequencia = NR_SEQUENCIA_P
    and
        UPPER(ie_status) = 'A'
    and
        obter_codigo_usuario(NM_USUARIO_P) = cd_pessoa_fisica;

    if (qt_registros > 0) then
        ret := 'S';
    else
        select nr_seq_doc into STRICT nr_seq_doc_w
        from  
            documento_aprov_prof
        where
            nr_sequencia = NR_SEQUENCIA_P;

        select count(*) into STRICT qt_registros
        from
            documento_aprov_prof
        where
            nr_seq_doc = nr_seq_doc_w 
        and
            UPPER(ie_status) = 'A'
        and
            obter_codigo_usuario(NM_USUARIO_P) = cd_pessoa_fisica;

        if (qt_registros > 0) then
            ret := 'A';
        else
            ret := 'N';
        end if;
    end if;
    RETURN ret;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
