CREATE OR REPLACE FUNCTION obter_se_consiste_atend_apac (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

 
			 
			 
retorno_w				varchar(2) := 'N';
nr_atendimento_w			bigint;
qt_existe_w				bigint;
cd_estab_usuario_w			integer := 0;
ie_consiste_com_alta_w			varchar(15) := 'S';
nm_usuario_w				varchar(15) := '';

c01 CURSOR FOR 
SELECT	nr_atendimento 
from	atendimento_paciente 
where	cd_pessoa_fisica = cd_pessoa_fisica_p 
and	(((ie_consiste_com_alta_w = 'S') and 
	obter_se_atendimento_alta(nr_atendimento) = 'N') or (ie_consiste_com_alta_w = 'T'));


BEGIN 
 
begin 
cd_estab_usuario_w 	:= wheb_usuario_pck.get_cd_estabelecimento;
nm_usuario_w		:= wheb_usuario_pck.get_nm_usuario;
exception 
when others then 
	cd_estab_usuario_w	:= 0;
	nm_usuario_w		:= '';	
end;
 
ie_consiste_com_alta_w	:= coalesce(obter_valor_param_usuario(916,574,obter_perfil_ativo,nm_usuario_w,cd_estab_usuario_w),'S');
 
open C01;
loop 
fetch C01 into	 
	nr_atendimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	select	count(*) 
	into STRICT	qt_existe_w 
	from	sus_apac_unif 
	where	nr_atendimento = nr_atendimento_w 
	and	clock_timestamp() <= dt_fim_validade 
	and	(ie_tipo_apac IS NOT NULL AND ie_tipo_apac::text <> '');
	 
	if (qt_existe_w > 0) then 
		retorno_w	:=	'S';
	end if;
	end;
end loop;
close C01;
 
return	retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
