CREATE OR REPLACE FUNCTION obter_se_consiste_beira ( nr_prescricao_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_seq_proc_interno_p bigint) RETURNS varchar AS $body$
DECLARE


cd_area_proc_w			bigint;
cd_grupo_proc_w			bigint;
cd_espec_proc_w			bigint;
cont_w					bigint;
cont_jus_w				bigint;
ie_consistir_w			varchar(1) := 'N';
cd_setor_atendimento_w		integer;


BEGIN

select 	coalesce(max(cd_area_procedimento),0),
	coalesce(max(cd_especialidade),0),
	coalesce(max(cd_grupo_Proc),0)
into STRICT	cd_area_proc_w,
	cd_espec_proc_w,
	cd_grupo_Proc_w
from	estrutura_Procedimento_v
where	cd_procedimento		= cd_procedimento_p
and	ie_origem_proced	= ie_origem_proced_p;

select	max(cd_setor_atendimento)
into STRICT	cd_setor_atendimento_w
from	prescr_medica
where	nr_prescricao = nr_prescricao_p;

select	count(*)
into STRICT	cont_w
from	regra_proced_beira_leito
where	coalesce(cd_procedimento,coalesce(cd_procedimento_p,0))		= coalesce(cd_procedimento_p,0)
and	((coalesce(cd_procedimento::text, '') = '') or (ie_origem_proced = ie_origem_proced_p))
and	coalesce(cd_area_procedimento,cd_area_proc_w)	= cd_area_proc_w
and	coalesce(cd_especialidade,cd_espec_proc_w)		= cd_espec_proc_w
and	coalesce(cd_grupo_proc,cd_grupo_Proc_w)		= cd_grupo_Proc_w
and coalesce(coalesce(nr_seq_proc_interno,nr_seq_proc_interno_p),0)	= coalesce(nr_seq_proc_interno_p,0)
and	coalesce(ie_situacao,'A')	= 'A'
and	consistir_setor_proc_leito(nr_sequencia,cd_setor_atendimento_w) = 'S';

select	count(*)
into STRICT	cont_jus_w
from	regra_proced_beira_leito
where	coalesce(cd_procedimento,coalesce(cd_procedimento_p,0))		= coalesce(cd_procedimento_p,0)
and	((coalesce(cd_procedimento::text, '') = '') or (coalesce(ie_origem_proced,ie_origem_proced_p) = ie_origem_proced_p))
and	coalesce(cd_area_procedimento,cd_area_proc_w)	= cd_area_proc_w
and	coalesce(cd_especialidade,cd_espec_proc_w)		= cd_espec_proc_w
and	coalesce(cd_grupo_proc,cd_grupo_Proc_w)		= cd_grupo_Proc_w
and coalesce(coalesce(nr_seq_proc_interno,nr_seq_proc_interno_p),0)	= coalesce(nr_seq_proc_interno_p,0)
and	coalesce(ie_situacao,'A')	= 'A'
and	coalesce(ie_exige_justificativa,'N')	= 'S'
and	consistir_setor_proc_leito(nr_sequencia,cd_setor_atendimento_w) = 'S';

if (cont_jus_w > 0) then
	ie_consistir_w	:= 'J';
elsif (cont_w > 0)  then
	ie_consistir_w := 'S';
end if;

return	ie_consistir_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
