CREATE OR REPLACE FUNCTION obter_se_consiste_bloq_visita ( nr_atendimento_p bigint, cd_pessoa_fisica_p text, nm_pessoa_bloqueio_p text, nr_identidade_p text) RETURNS varchar AS $body$
DECLARE


qt_regra_w	bigint 	:= 0;
ds_retorno_w	varchar(1)	:= 'N';


BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	select 	count(*)
	into STRICT	qt_regra_w
	from 	atendimento_visita_bloq
	where 	nr_atendimento 		= nr_atendimento_p
	and 	cd_pessoa_bloqueio 	= cd_pessoa_fisica_p;
end if;

if (qt_regra_w = 0) then
	select	count(*)
	into STRICT	qt_regra_w
	from 	atendimento_visita_bloq
	where 	nr_atendimento	 	= 	nr_atendimento_p
	and	nm_pessoa_bloqueio	= 	nm_pessoa_bloqueio_p
	and	((nr_identidade		= 	nr_identidade_p and (nr_identidade_p IS NOT NULL AND nr_identidade_p::text <> '')) or (coalesce(nr_identidade::text, '') = '' and coalesce(nr_identidade_p::text, '') = ''));
end if;

if (qt_regra_w	> 0) then
	ds_retorno_w	:= 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
