CREATE OR REPLACE FUNCTION obter_se_consiste_cid ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_consistir_w	varchar(1) := 'N';
cont_w		bigint;


BEGIN

select	count(*)
into STRICT	cont_w
from	diagnostico_doenca
where	ie_classificacao_doenca	= 'P'
and	nr_atendimento	= nr_atendimento_p
and	trunc(dt_liberacao,'dd') >= trunc(clock_timestamp(),'dd')
and	coalesce(dt_inativacao::text, '') = '';

if (cont_w = 0) then
	ie_consistir_w	:= 'S';
end if;

return	ie_consistir_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
