CREATE OR REPLACE FUNCTION obter_se_consiste_conta2 ( nr_interno_conta_p bigint, cd_material_p bigint, qt_material_p bigint, dt_item_p timestamp) RETURNS varchar AS $body$
DECLARE

 
qt_erro_w  		bigint := 0;
qt_item_w		bigint;
qt_desconto_w		bigint;
ie_retorno_w		varchar(1) := 'N';
			

BEGIN 
 
if (nr_interno_conta_p IS NOT NULL AND nr_interno_conta_p::text <> '') and (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then 
	 
	begin 
	 
	select	sum(qt_item) 
	into STRICT	qt_item_w 
	from	conta_paciente_v 
	where	nr_interno_conta = nr_interno_conta_p 
	and	coalesce(nr_seq_proc_pacote, nr_sequencia) = nr_sequencia 
	and 	coalesce(ie_cancelamento::text, '') = '' 
	and	coalesce(cd_motivo_exc_conta::text, '') = '' 
	and	trunc(dt_item,'dd')	= trunc(dt_item_p,'dd') 
	and	cd_item = cd_material_p;
	exception 
		when others then 
		qt_item_w := 0;
	end;
 
	qt_item_w := qt_item_w + coalesce(qt_material_p,0);
 
	select	count(*) 
	into STRICT	qt_desconto_w 
	from	conta_paciente_desconto 
	where	nr_interno_conta	= nr_interno_conta_p;
 
	if (qt_desconto_w = 0) and (qt_item_w < 0) then 
		ie_retorno_w := 'S';
	end if;
	 
end if;	
 
return ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
