CREATE OR REPLACE FUNCTION obter_se_consiste_gasoterapia ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_consiste_gasoterapia_w	varchar(1) := 'N';


BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	begin
		select	coalesce(max('S'),'N')
		into STRICT	ie_consiste_gasoterapia_w
		from	prescr_gasoterapia a
		where	substr(obter_status_gasoterapia(a.nr_sequencia,'C'),1,80) in ('I','R','IN')
		and	exists (SELECT	1
				from	prescr_medica b
				where	b.nr_atendimento = nr_atendimento_p
				and	b.nr_prescricao = a.nr_prescricao);

	end;
end if;

return ie_consiste_gasoterapia_w;

end;
$body$
