CREATE OR REPLACE FUNCTION obter_se_consiste_laudo_aih ( nr_atendimento_p bigint, nr_horas_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_consistir_w		varchar(1) := 'N';
cont_w			bigint;
ie_tipo_convenio_w	smallint;
dt_entrada_w		timestamp;
nr_horas_w		bigint;


BEGIN 
 
select	max(Obter_Tipo_Convenio(obter_convenio_atendimento(nr_atendimento_p))) 
into STRICT	ie_tipo_convenio_w
;
 
select	max(dt_entrada) 
into STRICT	dt_entrada_w 
from	atendimento_paciente 
where	nr_atendimento	= nr_atendimento_p;
 
nr_horas_w	:= obter_hora_entre_datas(dt_entrada_w, clock_timestamp());
 
if (ie_tipo_convenio_w = 3) and (nr_horas_w >= nr_horas_p) then 
 
	select	count(*) 
	into STRICT	cont_w 
	from	sus_laudo_paciente 
	where	nr_atendimento	= nr_atendimento_p 
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');
 
	if (cont_w = 0) then 
		ie_consistir_w	:= 'S';
	end if;
 
end if;
 
return	ie_consistir_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
