SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_consiste_min_fornec ( nr_cot_compra_p bigint, ie_retorno_p text) RETURNS varchar AS $body$
DECLARE

/*ie_retorno_p 
C - Consiste (Sim ou Não) 
Q - Quantidade mínimo de fornecedores necessários*/
 
			 
 
ds_retorno_w			varchar(255);
ie_consiste_w			varchar(1);
qt_minimo_fornec_w		bigint;
cd_estabelecimento_w		bigint;
qt_fornecedores_w		bigint;
vl_total_cotacao_w		double precision;
	
c01 CURSOR FOR	 
SELECT	qt_minimo_fornec 
from	regra_qt_minima_fornec_cot 
where	cd_estabelecimento = cd_estabelecimento_w 
and	vl_total_cotacao_w between vl_minimo and vl_maximo;


BEGIN 
ie_consiste_w := 'N';
 
select	cd_estabelecimento 
into STRICT	cd_estabelecimento_w 
from	cot_compra 
where	nr_cot_compra = nr_cot_compra_p;
 
select	sum(vl_preco_liquido) 
into STRICT	vl_total_cotacao_w 
from	cot_compra_resumo_v 
where	nr_cot_compra = nr_cot_compra_p;
 
open C01;
loop 
fetch C01 into	 
	qt_minimo_fornec_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	qt_minimo_fornec_w := qt_minimo_fornec_w;
	end;
end loop;
close C01;
 
if (qt_minimo_fornec_w > 0) then 
 
	select	count(distinct a.cd_cgc_fornecedor) 
	into STRICT	qt_fornecedores_w 
	from	cot_compra_forn a, 
		cot_compra_forn_item b 
	where	a.nr_sequencia = b.nr_seq_cot_forn 
	and	a.nr_cot_compra = nr_cot_compra_p 
	and	vl_unitario_material > 0;
	 
	if (qt_fornecedores_w < qt_minimo_fornec_w) then 
		ie_consiste_w := 'S';
	end if;
end if;
 
if (ie_retorno_p = 'C') then 
	ds_retorno_w := ie_consiste_w;
elsif (ie_retorno_p = 'Q') then 
	ds_retorno_w := qt_minimo_fornec_w;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
