CREATE OR REPLACE FUNCTION obter_se_consiste_min_pausa ( nr_prescricao_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_consistir_w		varchar(1) := 'N';
nr_seq_evento_w		bigint;
qt_min_pausa_w		bigint;
dt_evento_w		timestamp;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_evento_w
from	prescr_solucao_evento
where	nr_prescricao = nr_prescricao_p
and	nr_seq_material = nr_sequencia_p
and	coalesce(qt_min_pausa,0) > 0;

select	coalesce(max(qt_min_pausa),0),
	max(dt_alteracao)
into STRICT	qt_min_pausa_w,
	dt_evento_w
from	prescr_solucao_evento
where	nr_sequencia = nr_seq_evento_w;

if (qt_min_pausa_w = 0) or (clock_timestamp() > dt_evento_w + qt_min_pausa_w/1440) then
	ie_consistir_w	:= 'S';
end if;

return	ie_consistir_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
