SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_consiste_regra_nf ( cd_estabelecimento_p bigint, cd_material_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_opcao_p
A - ie_exige_atendimento
C - ie_consiste_vl_compra
P - pr_variacao_valor
M - ie_exige_marca
F - ie_exige_fabricante
CA - ie_exige_ca
AN - ie_exige_anvisa
V - ie_exige_aval_solic_servico
*/
cd_grupo_material_w		smallint;
cd_subgrupo_material_w		smallint;
cd_classe_material_w		integer;
ie_exige_atendimento_w		varchar(1) := 'N';
ie_exige_marca_w			varchar(1) := 'N';
ie_exige_fabric_w			varchar(1) := 'N';
ie_exige_ca_w			varchar(1) := 'N';
ie_exige_anvisa_w			varchar(1) := 'N';
qt_existe_w			bigint;
ie_consiste_vl_compra_w		varchar(1);
pr_variacao_valor_w		varchar(3);
ds_retorno_w			varchar(100);
ie_exige_aval_solic_servico_w	sup_regra_consistencia_nf.ie_exige_aval_solic_servico%type;

c01 CURSOR FOR
SELECT	coalesce(ie_exige_atendimento,'N'),
	coalesce(ie_consiste_vl_compra,'N'),
	coalesce(ie_exige_marca,'N'),
	coalesce(ie_exige_fabricante,'N'),
	coalesce(ie_exige_ca,'N'),
	coalesce(ie_exige_anvisa,'N'),
	coalesce(ie_exige_aval_solic_servico,'N'),
	pr_variacao_valor
from	sup_regra_consistencia_nf
where	coalesce(cd_grupo_material, cd_grupo_material_w)		= cd_grupo_material_w
and	coalesce(cd_subgrupo_material, cd_subgrupo_material_w)	= cd_subgrupo_material_w
and	coalesce(cd_classe_material, cd_classe_material_w)		= cd_classe_material_w
and (coalesce(cd_material, cd_material_p) 			= cd_material_p or cd_material_p = 0)
and	coalesce(ie_situacao,'A')					= 'A'
and	cd_estabelecimento					= cd_estabelecimento_p
order by
	coalesce(cd_material, 0),
	coalesce(cd_classe_material, 0),
	coalesce(cd_subgrupo_material, 0),
	coalesce(cd_grupo_material, 0);


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	sup_regra_consistencia_nf
where	cd_estabelecimento = cd_estabelecimento_p
and	coalesce(ie_situacao,'A') = 'A';

if (qt_existe_w > 0) then

	begin
	select	cd_grupo_material,
		cd_subgrupo_material,
		cd_classe_material
	into STRICT	cd_grupo_material_w,
		cd_subgrupo_material_w,
		cd_classe_material_w
	from	estrutura_material_v
	where	cd_material = cd_material_p;

	open	c01;
	loop
	fetch c01 into
		ie_exige_atendimento_w,
		ie_consiste_vl_compra_w,
		ie_exige_marca_w,
		ie_exige_fabric_w,
		ie_exige_ca_w,
		ie_exige_anvisa_w,
		ie_exige_aval_solic_servico_w,
		pr_variacao_valor_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	end loop;
	close c01;

	exception when others then
		ie_exige_atendimento_w		:= 'N';
		ie_consiste_vl_compra_w		:= 'N';
		ie_exige_marca_w		:= 'N';
		ie_exige_fabric_w		:= 'N';
		ie_exige_ca_w			:= 'N';
		ie_exige_anvisa_w		:= 'N';
		pr_variacao_valor_w		:= '';
		ie_exige_aval_solic_servico_w	:= 'N';
	end;

end if;

if (ie_opcao_p = 'A') then
	ds_retorno_w := ie_exige_atendimento_w;
elsif (ie_opcao_p = 'C') then
	ds_retorno_w := ie_consiste_vl_compra_w;
elsif (ie_opcao_p = 'CA') then
	ds_retorno_w := ie_exige_ca_w;
elsif (ie_opcao_p = 'AN') then
	ds_retorno_w := ie_exige_anvisa_w;
elsif (ie_opcao_p = 'P') then
	ds_retorno_w := pr_variacao_valor_w;
elsif (ie_opcao_p = 'M') then
	ds_retorno_w := ie_exige_marca_w;
elsif (ie_opcao_p = 'F') then
	ds_retorno_w := ie_exige_fabric_w;
elsif (ie_opcao_p = 'V') then
	ds_retorno_w := ie_exige_aval_solic_servico_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
