SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_consiste_resp_cred (nr_cirurgia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w				varchar(1);
ie_responsavel_credito_w	varchar(5);

C01 CURSOR FOR
	SELECT   ie_responsavel_credito
	from     procedimento_paciente
	where    nr_cirurgia = nr_cirurgia_p
	and		 coalesce(cd_motivo_exc_conta::text, '') = '';



BEGIN
ds_retorno_w := 'N';

open C01;
loop
fetch C01 into
	ie_responsavel_credito_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (coalesce(ie_responsavel_credito_w::text, '') = '') then
		ds_retorno_w := 'S';
	end if;
	end;
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
