CREATE OR REPLACE FUNCTION obter_se_consistir_regra_proc ( cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_prescricao_p bigint, ie_lado_p text) RETURNS varchar AS $body$
DECLARE


ie_permite_prescricao_w	varchar(1) := 'S';
cont_w			bigint;
cd_area_procedimento_w	bigint;
cd_espec_proced_w	bigint;
cd_grupo_proc_w		bigint;
cd_convenio_w		integer;
qt_idade_w		smallint;
cd_pessoa_fisica_w	varchar(15);

c01 CURSOR FOR
SELECT	ie_permite_prescricao
from	rep_regra_restricao_proc
where	coalesce(cd_procedimento, cd_procedimento_p)		= cd_procedimento_p
and	((coalesce(cd_procedimento::text, '') = '') or (ie_origem_proced = ie_origem_proced_p))
and	coalesce(cd_area_procedimento, cd_area_procedimento_w)	= cd_area_procedimento_w
and	coalesce(cd_especialidade, cd_espec_proced_w)		= cd_espec_proced_w
and	coalesce(cd_grupo_proc, cd_grupo_proc_w)			= cd_grupo_proc_w
and	((coalesce(cd_convenio::text, '') = '') or (cd_convenio = cd_convenio_w))
and	((coalesce(ie_lado::text, '') = '') or (ie_lado = ie_lado_p))
and 	coalesce(qt_idade_w, 0) between coalesce(qt_idade_min, 0) and coalesce(qt_idade_max, 999)
order by coalesce(cd_convenio,0),
	coalesce(cd_area_procedimento,0),
	coalesce(cd_especialidade,0),
	coalesce(cd_grupo_proc,0),
	coalesce(cd_procedimento,0);


BEGIN

select	count(*)
into STRICT	cont_w
from	rep_regra_restricao_proc;

if (cont_w > 0) then
	select	coalesce(max(cd_area_procedimento),0),
		coalesce(max(cd_especialidade),0),
		coalesce(max(cd_grupo_proc),0)
	into STRICT	cd_area_procedimento_w,
		cd_espec_proced_w,
		cd_grupo_proc_w
	from	estrutura_procedimento_v
	where	cd_procedimento = cd_procedimento_p
	and	ie_origem_proced = ie_origem_proced_p;

	select	max(obter_convenio_atendimento(nr_atendimento)),
		max(cd_pessoa_fisica)
	into STRICT	cd_convenio_w,
		cd_pessoa_fisica_w
	from	prescr_medica
	where	nr_prescricao = nr_prescricao_p;

	select	max(obter_idade_pf(cd_pessoa_fisica_w, clock_timestamp(), 'A'))
	into STRICT	qt_idade_w
	;

	open C01;
	loop
	fetch C01 into
		ie_permite_prescricao_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		ie_permite_prescricao_w := ie_permite_prescricao_w;
	end loop;
	close C01;

end if;

return	ie_permite_prescricao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
