SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_consist_regra_mat ( nr_sequencia_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


qt_registro_w			bigint;
ie_situacao_w			varchar(1);
ie_existe_w			varchar(1) := 'N';
nr_seq_familia_w			bigint;
cd_grupo_material_w		bigint;
cd_subgrupo_material_w		bigint;
cd_classe_material_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_registro_w
from	cot_regra_consist_calc_est
where	nr_seq_regra = nr_sequencia_p;

select	coalesce(ie_situacao,'A')
into STRICT	ie_situacao_w
from	cot_regra_consist_calc
where	nr_sequencia = nr_sequencia_p;

if (qt_registro_w = 0) and (ie_situacao_w = 'A') then

	ie_existe_w	:= 'S';
else

	select	max(a.cd_grupo_material),
		max(a.cd_subgrupo_material),
		max(a.cd_classe_material),
		max(nr_seq_familia)
	into STRICT	cd_grupo_material_w,
		cd_subgrupo_material_w,
		cd_classe_material_w,
		nr_seq_familia_w
	from	estrutura_material_v a
	where	a.cd_material	= cd_material_p;

	select	count(*)
	into STRICT	qt_registro_w
	from	cot_regra_consist_calc_est
	where	nr_seq_regra = nr_sequencia_p
	and	coalesce(cd_grupo_material, cd_grupo_material_w)		= cd_grupo_material_w
	and	coalesce(cd_subgrupo_material, cd_subgrupo_material_w) 	= cd_subgrupo_material_w
	and	coalesce(cd_classe_material, cd_classe_material_w)		= cd_classe_material_w
	and	coalesce(cd_material, cd_material_p)			= cd_material_p
	and	((coalesce(nr_seq_familia, nr_seq_familia_w)			= nr_seq_familia_w) or (coalesce(nr_seq_familia::text, '') = ''));

	if (qt_registro_w > 0) and (ie_situacao_w = 'A') then

		ie_existe_w	:= 'S';
	end if;
end if;

return	ie_existe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
