CREATE OR REPLACE FUNCTION obter_se_consis_val_indeterm ( ie_funcao_p text, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE



/*
ie_funcao_p
NF	- Ccnsiste na nota fiscal
IRPDA	- Consiste na inspeção recebimento PDA
*/
ie_tipo_servico_w			varchar(15);
nr_seq_regra_w				bigint;
cd_grupo_material_w			smallint;
cd_subgrupo_material_w			smallint;
cd_classe_material_w			integer;
ie_permite_indeterm_w			varchar(1) := 'S';
cd_estabelecimento_w			integer;


c01 CURSOR FOR
SELECT	coalesce(ie_permite_indeterm,'S')
from	regra_validade_indeterm
where	coalesce(cd_grupo_material, cd_grupo_material_w)		= cd_grupo_material_w
and	coalesce(cd_subgrupo_material, cd_subgrupo_material_w)	= cd_subgrupo_material_w
and	coalesce(cd_classe_material, cd_classe_material_w)		= cd_classe_material_w
and (coalesce(cd_material, cd_material_p) 			= cd_material_p or cd_material_p = 0)
and	((ie_funcao_p = 'NF' AND ie_consiste_nf = 'S') or
	 (ie_funcao_p = 'IRPDA' AND ie_consiste_insp_pda = 'S'))
order by
	coalesce(cd_material, 0),
	coalesce(cd_classe_material, 0),
	coalesce(cd_subgrupo_material, 0),
	coalesce(cd_grupo_material, 0);


BEGIN

select	a.cd_grupo_material,
	a.cd_subgrupo_material,
	a.cd_classe_material
into STRICT	cd_grupo_material_w,
	cd_subgrupo_material_w,
	cd_classe_material_w
from	estrutura_material_v a
where	a.cd_material = cd_material_p;


open C01;
loop
fetch C01 into
	ie_permite_indeterm_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ie_permite_indeterm_w	:= ie_permite_indeterm_w;
	end;
end loop;
close C01;

return ie_permite_indeterm_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
