CREATE OR REPLACE FUNCTION obter_se_consite_proc_setor (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_consistir_w		varchar(1);


BEGIN

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_consistir_w
from	setor_atendimento a,
	prescr_procedimento b,
	prescr_medica c
where	c.nr_prescricao		= b.nr_prescricao
and	b.cd_setor_atendimento	= a.cd_setor_atendimento
and	coalesce(a.ie_permite_alta_pend,'S')	= 'N'
and	c.nr_atendimento	= nr_atendimento_p
and	b.ie_status_execucao	= 10
and	c.dt_prescricao		> clock_timestamp() - interval '30 days';

return	ie_consistir_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
