CREATE OR REPLACE FUNCTION obter_se_consulta_assinatura ( nr_seq_projeto_p bigint, cd_perfil_p bigint) RETURNS char AS $body$
DECLARE


ie_consulta_automatica_w	char(1);
cd_perfil_w					perfil.cd_perfil%type;

c01 CURSOR FOR
SELECT	coalesce(a.ie_consulta_automatica,'S')
from	tasy_proj_assin_perfil a
where	a.nr_seq_proj		= nr_seq_projeto_p
and		coalesce(a.cd_perfil, cd_perfil_w) = cd_perfil_w
order by
		coalesce(a.cd_perfil,0);


BEGIN

cd_perfil_w	:= coalesce(cd_perfil_p,0);

open c01;
loop
fetch c01 into
	ie_consulta_automatica_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	ie_consulta_automatica_w := ie_consulta_automatica_w;
end loop;
close c01;

return ie_consulta_automatica_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
