CREATE OR REPLACE FUNCTION obter_se_consulta_medicina (nr_seq_agend_consulta_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w 	varchar(2);				



BEGIN

if (nr_seq_agend_consulta_p IS NOT NULL AND nr_seq_agend_consulta_p::text <> '')then

	select	CASE WHEN coalesce(max(b.nr_sequencia)::text, '') = '' THEN   'N'  ELSE 'S' END
	into STRICT	ds_retorno_w 
	from	agenda_consulta a,
			agenda_consulta_telemed b
	where	a.nr_sequencia = b.nr_seq_agenda
	and		a.nr_sequencia = nr_seq_agend_consulta_p;

end if;


return	coalesce(ds_retorno_w, 'N');

end;
