CREATE OR REPLACE FUNCTION obter_se_cons_impl_modulos ( nr_seq_consultor_p bigint, ds_lista_modulos_p text) RETURNS varchar AS $body$
DECLARE


ie_impl_modulos_w	varchar(1) := 'N';
ds_lista_modulos_w	varchar(1000);
nr_pos_virgula_w	smallint;
nr_seq_modulo_w		bigint;
ie_impl_modulos_ww	varchar(1) := 'N';


BEGIN
if (nr_seq_consultor_p IS NOT NULL AND nr_seq_consultor_p::text <> '') and (ds_lista_modulos_p IS NOT NULL AND ds_lista_modulos_p::text <> '') then
	begin
	ds_lista_modulos_w := ds_lista_modulos_p;
	ie_impl_modulos_ww := 'S';
	while(ie_impl_modulos_ww = 'S') and (ds_lista_modulos_w IS NOT NULL AND ds_lista_modulos_w::text <> '') loop
		begin
		nr_pos_virgula_w := position(',' in ds_lista_modulos_w);
		if (nr_pos_virgula_w > 0) then
			begin
			nr_seq_modulo_w		:= (substr(ds_lista_modulos_w,1,nr_pos_virgula_w-1))::numeric;
			ds_lista_modulos_w	:= substr(ds_lista_modulos_w,nr_pos_virgula_w+1,length(ds_lista_modulos_w));
			end;
		else
			begin
			nr_seq_modulo_w		:= (ds_lista_modulos_w)::numeric;
			ds_lista_modulos_w	:= null;
			end;
		end if;

		if (nr_seq_modulo_w > 0) then
			begin
			select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
			into STRICT	ie_impl_modulos_ww
			from	com_canal_cons_modulo
			where	nr_seq_consultor = nr_seq_consultor_p
			and	nr_seq_modulo_implant = nr_seq_modulo_w;
			end;
		else
			begin
			ie_impl_modulos_ww := 'N';
			end;
		end if;
		end;
	end loop;
	end;
end if;
ie_impl_modulos_w := ie_impl_modulos_ww;
return ie_impl_modulos_w;
end;
$body$
