CREATE OR REPLACE FUNCTION obter_se_cons_tempo_processo ( cd_perfil_p bigint, cd_setor_atendimento_p bigint, nr_seq_processo_p bigint) RETURNS varchar AS $body$
DECLARE


ie_consiste_w		varchar(1) := 'S';
qt_minutos_w		smallint;
dt_horario_processo_w		timestamp;


BEGIN

select	coalesce(max(qt_minutos),0)
into STRICT	qt_minutos_w
from	pda_regra_tempo_processo
where	coalesce(cd_perfil,coalesce(cd_perfil_p,0)) = coalesce(cd_perfil_p,0)
and		coalesce(cd_setor_atendimento,coalesce(cd_setor_atendimento_p,0)) = coalesce(cd_setor_atendimento_p,0);

if (qt_minutos_w > 0) then
	select	max(dt_horario_processo)
	into STRICT	dt_horario_processo_w
	from	adep_processo
	where	nr_sequencia = nr_seq_processo_p;

	if	((dt_horario_processo_w - (qt_minutos_w/1440)) < clock_timestamp()) then
		ie_consiste_w := 'N';
	end if;
end if;

return	ie_consiste_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
