SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_contagens_corretas ( nr_seq_registro_w bigint) RETURNS varchar AS $body$
DECLARE


ie_corretas_w		varchar(1) := 'N';
qt_registros_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_registros_w
from	inspecao_contagem
where	nr_seq_registro = nr_seq_registro_w
and	nr_seq_contagem = 2
and	coalesce(dt_liberacao::text, '') = '';

	/*Quando todos os registros da segunda contagem estão liberados.*/

if (qt_registros_w = 0) then

	select	count(*)
	into STRICT	qt_registros_w
	from	insp_contagem_consiste a,
		inspecao_contagem b
	where	a.nr_seq_inspecao = b.nr_seq_inspecao
	and	b.nr_seq_registro = nr_seq_registro_w;

	/*Se tem consistencias entao ie_correta = N*/

	if (qt_registros_w > 0) then
		ie_corretas_w := 'N';
	else
	/*Se não tem consistencias entao ie_correta = S*/

		ie_corretas_w := 'S';
	end if;
end if;

return	ie_corretas_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
