SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_conta_aber_part ( nr_atendimento_p bigint, nr_prescricao_p bigint, cd_setor_p bigint) RETURNS varchar AS $body$
DECLARE

ie_retorno 		varchar(1);
nr_atendimento_w	bigint;
nr_interno_conta_w	bigint;
nr_seq_protocolo_w	bigint;

C01 CURSOR FOR 
	SELECT	a.nr_interno_conta, 
			a.nr_seq_protocolo 
	from	conta_paciente a, 
			atendimento_paciente b 
	where	a.nr_atendimento = nr_atendimento_w 
	and		a.nr_atendimento = b.nr_atendimento 
	and   b.ie_tipo_convenio = 1;


BEGIN 
 
if (coalesce(nr_atendimento_p::text, '') = '') and (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then 
	begin 
	select 	a.nr_atendimento 
	into STRICT	nr_atendimento_w 
	from 	prescr_medica a 
	where 	a.nr_prescricao = nr_prescricao_p;
	end;
end if;
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
	begin 
	nr_atendimento_w := nr_atendimento_p;
	end;
end if;
 
ie_retorno := 'N';
 
open C01;
loop 
fetch C01 into 
	nr_interno_conta_w, 
	nr_seq_protocolo_w;
exit when(C01%notfound or ie_retorno = 'S');
	begin	 
	 
	select	CASE WHEN  sum(vl_recebido)=0 THEN 'S'  ELSE 'N' END  vl_recebido 
	into STRICT	ie_retorno 
			from (	SELECT	coalesce(sum(vl_recebido),0) vl_recebido 
					from	titulo_receber a, 
							titulo_receber_liq b 
					where  a.nr_titulo = b.nr_titulo 
					and   a.nr_interno_conta = nr_interno_conta_w 
					
union
 
					SELECT coalesce(sum(vl_recebido),0) vl_recebido 
					from  titulo_receber a, 
							titulo_receber_liq b 
					where  a.nr_titulo = b.nr_titulo 
					and   a.nr_seq_protocolo = nr_seq_protocolo_w 
					and   coalesce(a.nr_interno_conta::text, '') = '' 
				) alias6;
	end;		
end loop;
close c01;	
 
if (ie_retorno = 'S') then 
	begin 
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
	into STRICT	ie_retorno 
	from	regra_pend_conta_atend 
	where	cd_setor_atendimento = cd_setor_p;
	end;
end if;
 
return	ie_retorno;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
