CREATE OR REPLACE FUNCTION obter_se_conta_cancelada ( nr_interno_conta_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_cancelamento_w	varchar(1);


BEGIN 
 
begin 
select	max(ie_cancelamento) 
into STRICT	ie_cancelamento_w 
from	conta_paciente 
where	nr_interno_conta	= nr_interno_conta_p;
exception 
when others then 
	ie_cancelamento_w	:= null;
end;
 
if (coalesce(ie_cancelamento_w::text, '') = '') then 
	return 'N';
else 
	return 'S';
end if;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
