CREATE OR REPLACE FUNCTION obter_se_conta_conferida ( nr_interno_conta_p bigint) RETURNS varchar AS $body$
DECLARE

				
ie_conferencia_w		varchar(1)	:= 'N';
nr_sequencia_w			bigint;


BEGIN

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_sequencia_w
from	conta_paciente_conferencia
where	nr_interno_conta	= nr_interno_conta_p;

if (nr_sequencia_w	> 0) then
	select	coalesce(max(ie_conferencia),'N')
	into STRICT	ie_conferencia_w
	from	conta_paciente_conferencia
	where	nr_interno_conta	= nr_interno_conta_p
	and    	nr_sequencia = nr_sequencia_w;
end if;

return	ie_conferencia_w;

end;
