CREATE OR REPLACE FUNCTION obter_se_conta_consistente ( cd_estabelecimento_p bigint, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

	 
ie_conta_consistente_w		varchar(1) := 'N';
ie_fechar_conta_ps_alta_w	varchar(1);
ie_possui_conta_status_prov_w	varchar(1);
	

BEGIN 
if (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') and (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
	begin 
	 
	select	ie_fechar_conta_ps_alta 
	into STRICT	ie_fechar_conta_ps_alta_w 
	from	parametro_faturamento 
	where	cd_estabelecimento = cd_estabelecimento_p;
	 
	select	CASE WHEN coalesce(count(*),0)=0 THEN 'N'  ELSE 'S' END  
	into STRICT	ie_possui_conta_status_prov_w 
	from	conta_paciente 
	where	nr_atendimento 	 = nr_atendimento_p 
	and	ie_status_acerto = 1;
	 
	if (ie_fechar_conta_ps_alta_w = 'A') and (ie_possui_conta_status_prov_w = 'S') then 
		begin 
		ie_conta_consistente_w	:= 'N';
		end;
	else 
		begin 
		ie_conta_consistente_w 	:= 'S';
		end;
	end if;
	end;
end if;
return ie_conta_consistente_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
