CREATE OR REPLACE FUNCTION obter_se_conta_faec (nr_interno_conta_p bigint) RETURNS varchar AS $body$
DECLARE


C01 CURSOR FOR
	SELECT	cd_procedimento,
		ie_origem_proced,
		dt_procedimento
	from	procedimento_paciente a
	where	a.nr_interno_conta	= nr_interno_conta_p
	and	coalesce(a.cd_motivo_exc_conta::text, '') = '';

cd_procedimento_w		bigint;
ie_origem_proced_w		bigint;
dt_procedimento_w		timestamp;
ie_existe_w			varchar(10)	:= 'N';
cd_estabelecimento_w		smallint;


BEGIN
cd_estabelecimento_w	:= wheb_usuario_pck.get_cd_estabelecimento;
open C01;
loop
fetch C01 into
	cd_procedimento_w,
	ie_origem_proced_w,
	dt_procedimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	coalesce(max('S'),'N')
	into STRICT	ie_existe_w
	
	where	exists (	SELECT	1
				from	sus_faec_regra b,
					sus_faec a
				where	a.nr_sequencia		= b.nr_seq_faec
				and	a.cd_estabelecimento	=  cd_estabelecimento_w
				and	a.ie_sus_unificado	= 'S'
				and	b.cd_procedimento	= cd_procedimento_w
				and	b.ie_origem_proced	= ie_origem_proced_w
				and	dt_procedimento_w between coalesce(a.dt_inicio_vigencia,dt_procedimento_w) and coalesce(a.dt_final_vigencia,dt_procedimento_w));
	if (ie_existe_w	= 'S') then
		exit;
	end if;

	end;
end loop;
close C01;


return	ie_existe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
