CREATE OR REPLACE FUNCTION obter_se_conta_financ_estab (cd_conta_financ_p bigint, cd_estabelecimento_p bigint, ie_restringe_estab_p text) RETURNS varchar AS $body$
DECLARE


cd_empresa_estab_w		bigint;
cd_estab_exclusivo_w		bigint;
cd_empresa_conta_w		bigint;
ie_retorno_w			varchar(1);
cd_estab_financeiro_w		bigint;


BEGIN

-- Obter a empresa do estabelecimento corrente
select	cd_empresa
into STRICT	cd_empresa_estab_w
from	estabelecimento
where	cd_estabelecimento	 = cd_estabelecimento_p;

-- Obter o estabelecimento e a empresa da conta financeira
select	cd_estabelecimento,
	cd_empresa
into STRICT	cd_estab_exclusivo_w,
	cd_empresa_conta_w
from	conta_financeira
where	cd_conta_financ		= cd_conta_financ_p;

-- Obter estabelecimento financeiro do estabelecimento
if (coalesce(cd_estab_exclusivo_w,0) > 0) then
	select	cd_estab_financeiro
	into STRICT	cd_estab_financeiro_w
	from	estabelecimento
	where	cd_estabelecimento = cd_estab_exclusivo_w;
end if;


ie_retorno_w	:= 'N';

-- consistir se a empresa conta financ é da mesma empresa
if (coalesce(cd_empresa_conta_w, cd_empresa_estab_w) = cd_empresa_estab_w) then

	-- consistir se a conta financ tem estabelecimento exclusivo ou é do estabelecimento corrente
	if (coalesce(cd_estab_exclusivo_w,0) = 0) or (coalesce(cd_estab_exclusivo_w,cd_estabelecimento_p) = cd_estabelecimento_p) or (coalesce(cd_estab_financeiro_w,0) = cd_estabelecimento_p and ie_restringe_estab_p = 'S') then
		ie_retorno_w	:= 'S';
	end if;

end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
