CREATE OR REPLACE FUNCTION obter_se_conta_financ_perfil (cd_conta_financ_p bigint, cd_perfil_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w 		varchar(1);
qt_perfil_w		integer;
ie_perfil_liberado_w	varchar(1);


BEGIN

ds_retorno_w := 'N';

select 	count(*)
into STRICT 	qt_perfil_w
from 	conta_financ_perfil a
where 	a.cd_conta_financ = cd_conta_financ_p;

if (coalesce(qt_perfil_w,0) = 0) then
	ds_retorno_w := 'S';
else
	select 	coalesce(max('S'),'N')
	into STRICT 	ie_perfil_liberado_w
	from 	conta_financ_perfil a
	where 	a.cd_perfil = cd_perfil_p
	and 	a.cd_conta_financ = cd_conta_financ_p;

	if (ie_perfil_liberado_w = 'S') then
		ds_retorno_w := 'S';
	end if;
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
