SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_conta_financ_tf (nr_seq_trans_financ_p bigint, cd_conta_financ_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
cont_w		bigint;
qt_liberado_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_liberado_w
from	trans_financ_conta_fin a
where	a.nr_seq_trans_financ	= nr_seq_trans_financ_p;

if (qt_liberado_w > 0) then

	select	count(*)
	into STRICT	cont_w
	from	trans_financ_conta_fin
	where	cd_conta_financ		= cd_conta_financ_p
	and	nr_seq_trans_financ	= nr_seq_trans_financ_p;

end if;

ds_retorno_w			:= 'N';
if (qt_liberado_w = 0) or (cont_w > 0) then
	ds_retorno_w		:= 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
