CREATE OR REPLACE FUNCTION obter_se_conta_financ_tit ( nr_titulo_p bigint, cd_conta_financ_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*ie_opcao_p
TP - Título a pagar
TR - Título a receber
*/
cd_conta_financ_w	bigint;
ie_possui_conta_w	varchar(1);

C01 CURSOR FOR
SELECT	b.nr_seq_conta_financ
from	titulo_pagar_classif b,
	titulo_pagar a
where	a.nr_titulo = b.nr_titulo
and	a.nr_titulo = nr_titulo_p;

C02 CURSOR FOR
SELECT	b.cd_conta_financ
from	titulo_receber_classif b,
	titulo_receber a
where	a.nr_titulo = b.nr_titulo
and	a.nr_titulo = nr_titulo_p
and (b.cd_conta_financ IS NOT NULL AND b.cd_conta_financ::text <> '');


BEGIN
ie_possui_conta_w	:= 'N';

if (ie_opcao_p = 'TP') then
	open C01;
	loop
	fetch C01 into
		cd_conta_financ_w;
	EXIT WHEN NOT FOUND or ie_possui_conta_w = 'S';  /* apply on C01 */
		begin
		if (cd_conta_financ_w = cd_conta_financ_p) then
			ie_possui_conta_w	:= 'S';
		end if;
		end;
	end loop;
	close C01;
elsif (ie_opcao_p = 'TR') then
	open C02;
	loop
	fetch C02 into
		cd_conta_financ_w;
	EXIT WHEN NOT FOUND or ie_possui_conta_w = 'S';  /* apply on C02 */
		begin
		if (cd_conta_financ_w = cd_conta_financ_p) then
			ie_possui_conta_w	:= 'S';
		end if;
		end;
	end loop;
	close C02;
end if;


return	ie_possui_conta_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
