CREATE OR REPLACE FUNCTION obter_se_conta_origem (nr_seq_auditoria_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1):= 'N';
qt_reg_mat_w		bigint;
qt_reg_proc_w		bigint;
nr_interno_conta_w	bigint;


BEGIN

select 	max(nr_interno_conta)
into STRICT	nr_interno_conta_w
from 	auditoria_conta_paciente
where 	nr_sequencia = nr_seq_auditoria_p;

select 	count(*)
into STRICT	qt_reg_mat_w
from 	auditoria_matpaci
where 	nr_seq_auditoria = nr_seq_auditoria_p
and 	coalesce(nr_conta_origem,nr_interno_conta_w) <> nr_interno_conta_w;

select 	count(*)
into STRICT	qt_reg_proc_w
from 	auditoria_propaci
where 	nr_seq_auditoria = nr_seq_auditoria_p
and 	coalesce(nr_conta_origem,nr_interno_conta_w) <> nr_interno_conta_w;

if (qt_reg_proc_w + qt_reg_mat_w) > 0 then
	ie_retorno_w:= 'S';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
