CREATE OR REPLACE FUNCTION obter_se_conta_protocolo_nf ( nr_interno_conta_p bigint, nr_seq_protocolo_p bigint) RETURNS varchar AS $body$
DECLARE

qt_registros_w		bigint;
ds_retorno_w		varchar(1);


BEGIN

select	count(*)
into STRICT	qt_registros_w
from	nota_fiscal
where	nr_interno_conta	= nr_interno_conta_p;

if (qt_registros_w		= 0) and (nr_seq_protocolo_p IS NOT NULL AND nr_seq_protocolo_p::text <> '') then

	select	count(*)
	into STRICT	qt_registros_w
	from	nota_fiscal
	where	nr_seq_protocolo	= nr_seq_protocolo_p;
end if;

if (qt_registros_w		= 0) then
	ds_retorno_w	:= 'N';
else
	ds_retorno_w	:= 'S';
end if;


RETURN	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
