CREATE OR REPLACE FUNCTION obter_se_conta_protocolo (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

			 
ie_status_w	varchar(1) := 'N';
qt_contas_prot_w	bigint;


BEGIN 
 
if (coalesce(nr_atendimento_p,0) > 0) then 
 
	select	count(*) 
	into STRICT	qt_contas_prot_w 
	from	conta_paciente 
	where	nr_atendimento	= nr_atendimento_p 
	and 	coalesce(nr_seq_protocolo,0) > 0 
	and	ie_status_acerto	= 2;
	 
	if (qt_contas_prot_w > 0) then 
		ie_status_w	:= 'S';
	end if;
 
end if;
 
return	ie_status_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
