CREATE OR REPLACE FUNCTION obter_se_conta_prot_ant ( nr_interno_conta_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(1)	:= 'S';
qt_contas_ant_w		bigint	:= 0;
nr_atendimento_w	bigint;
cd_convenio_w		integer;


BEGIN 
 
if (coalesce(nr_interno_conta_p,0) > 0) then 
 
	select	max(nr_atendimento), 
		max(cd_convenio_parametro) 
	into STRICT	nr_atendimento_w, 
		cd_convenio_w 
	from	conta_paciente 
	where	nr_interno_conta = nr_interno_conta_p;
 
	select	count(*) 
	into STRICT	qt_contas_ant_w 
	from	conta_paciente 
	where	nr_atendimento = nr_atendimento_w 
	and	cd_convenio_parametro = cd_convenio_w 
	and	nr_interno_conta < nr_interno_conta_p 
	and	coalesce(nr_seq_protocolo::text, '') = '' 
	and	coalesce(ie_cancelamento::text, '') = '';
	 
	if (qt_contas_ant_w > 0) then 
		ds_retorno_w	:= 'N';
	end if;
 
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
